// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Microfinance {
    struct UserProfile {
        string name;
        address userAddress;
        uint256 balance;
    }

    mapping(address => UserProfile) private profiles;

    // Create or update a user profile
    function setUserProfile(string memory _name) public {
        profiles[msg.sender] = UserProfile(_name, msg.sender, 0);
    }

    // Get the user profile
    function getUserProfile(address _user) public view returns (string memory, address, uint256) {
        UserProfile memory profile = profiles[_user];
        return (profile.name, profile.userAddress, profile.balance);
    }

    // Add balance to user account
    function addBalance(uint256 _amount) public {
        profiles[msg.sender].balance += _amount;
    }
}
